Number.prototype.zeroPad = Number.prototype.zeroPad || 
     function(base){
       var nr = this, len = (String(base).length - String(nr).length)+1;
       return len > 0? new Array(len).join('0')+nr : nr;
};

function convert_datetime(value) {

    if (value == null) { return null }

    t1 = value
    year = t1.getFullYear().zeroPad(1000).toString()
    month = (t1.getMonth() + 1).zeroPad(10).toString()
    day = t1.getDate().zeroPad(10).toString()
    hour = t1.getHours().zeroPad(10).toString()
    minute = t1.getMinutes().zeroPad(10).toString()
    second = t1.getSeconds().zeroPad(10).toString()
    v = year + "-" + month + "-" + day + " " + hour + ":" + minute + ":" + second
    return v
} 

function convert_api_datetime(value) {

    if (value == null) { return null }

    t1 = value
    year = t1.getFullYear().zeroPad(1000).toString()
    month = (t1.getMonth() + 1).zeroPad(10).toString()
    day = t1.getDate().zeroPad(10).toString()
    hour = t1.getHours().zeroPad(10).toString()
    minute = t1.getMinutes().zeroPad(10).toString()
    second = t1.getSeconds().zeroPad(10).toString()
    v = year + month + day + hour + minute + second
    return v
} 



function convert_uuid(value) {

  if (value == null) { return null }

  let source = value;
  let raw = new Buffer.alloc(16, source, 'base64');

  let p1 = raw.subarray(0, 4);
  let p2 = raw.subarray(4, 6);
  let p3 = raw.subarray(6, 8);
  let p4 = raw.subarray(8, 10);
  let p5 = raw.subarray(10);

  let uuidArr = [
  p1.swap32().toString('hex'),
  p2.swap16().toString('hex'),
  p3.swap16().toString('hex'),
  p4.toString('hex'),
  p5.toString('hex')
  ];

  return uuidArr.join('-');

}



